const darkThemeHandler=()=>{const style=document.createElement("style");style.innerHTML=`\n        :root {\n            --bg-color-page: #19223e;\n            --bg-color-line: #364784;\n            --color-black: #cecece;\n            --color-brand-primary: #829CE8;\n            --bg-footer-1: #040606;\n            --bg-footer-2: #123844;\n        }\n\n        html {\n            background-color: var(--bg-color-page);\n        }\n\n        .c-logo span, .post-content blockquote {\n            background-color: var(--bg-color-line) !important;\n        }\n\n        .post-header__date {\n            color: var(--color-brand-primary);\n        }\n\n        .footer-copyrights, .social-title {\n            color: #fff;\n        }\n    `,document.body.appendChild(style)};darkThemeHandler();